sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading || newValue == '') {
		return;
	}

	// to get the field name of the choices you want to get.
	var choiceValue = g_form.getValue('urgency');
	// to get the present choice value. e.g. 1-low
	var choiceLabel = g_form.getOption('urgency', choiceValue).text; 
	// the start of ajax syntax. getUrgencyCount is the name of the SCRIPT INCLUDE.
	var urgencyAjax = new GlideAjax('getUrgencyCount');
	// the parameter or name of the function inside the SCRIPT INCLUDE.
	urgencyAjax.addParam('sysparm_name', 'getCount');
	// the parameter of the current choice of the dropdown menu.
	urgencyAjax.addParam('sysparm_value', newValue);
	// this is to get the function below.
	urgencyAjax.getXML(urgencyCount);

	function urgencyCount(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		// this is use to clear the answer first before displaying the next message.
		g_form.clearMessages();
		// this is the info.
		g_form.addInfoMessage(choiceLabel+' 同じ緊急度のインシデントが他にも '+answer+' 件あります.');
	}	

}
