sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading || newValue == '') {
		return;
	}

	var choiceValue = g_form.getValue('urgency');
	var choiceLabel = g_form.getOption('urgency', choiceValue).text; 	
	var urgencyAjax = new GlideAjax('getUrgencyCount');
	urgencyAjax.addParam('sysparm_name', 'getCount');
	urgencyAjax.addParam('sysparm_value', newValue);
	urgencyAjax.getXML(urgencyCount);

	function urgencyCount(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearMessages();
		g_form.addInfoMessage(choiceLabel+' 同じ緊急度のインシデントが他にも '+answer+' 件あります.');
	}	

}
