sys_script_include.script:
var getUrgencyCount = Class.create();

getUrgencyCount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCount: function(){
	var gr = new GlideRecord('incident');
	var urgencyValue = this.getParameter('sysparm_value');

	if(urgencyValue == 1){
		gr.addQuery('urgency', '1');
		gr.query();
		return gr.getRowCount();
	}

	else if(urgencyValue == 2){
		gr.addQuery('urgency', '2');
		gr.addQuery('active', '=', 'true');
		gr.query();
		return gr.getRowCount();	   
	}

	else if(urgencyValue == 3){
		gr.addQuery('urgency', '3');
		gr.query();
		return gr.getRowCount();	   
	}

	},

type: 'getUrgencyCount'

});
